public abstract class ASTAlias extends ASTNode {
	public static ASTAlias[]	emptyArray = new ASTAlias[0];
	
	public ASTAlias(int id) {
		super(0);
	}

	public abstract void attach(ASTNode n);
}

public class ASTIdentifierAlias extends ASTAlias {

	ASTIdentifier	name;

	public ASTIdentifierAlias(int id) {
		super(id);
	}

	public void jjtAddChild(ASTNode n, int i) {
		if( i==0 && n instanceof ASTIdentifier ) {
			name = (ASTIdentifier)n;
		}
		else
			throw new CompilerException(n.getPos(),"Bad child number "+i+": "+n);
    }

	public void attach(ASTNode n) {
		switch(n) {
		case Method:
			((Method)n).name.addAlias(name.name);
			break;
		case Field:
			((Field)n).name.addAlias(name.name);
			break;
		default:
			throw new CompilerException(pos,"Node of type "+n.getClass()+" cannot have aliases");
		}
	}

	public String toString() {
		return name.toString();
	}
    
	public Dumper toJava(Dumper dmp) {
		return dmp.space().append("/* alias ").append(name).append(" */").space();
	}

}
