public class ASTOpdef extends ASTAlias {
	public int					prior;
	public int					opmode;
	public KString				image;

	public ASTOpdef(int id) {
		super(0);
	}

	public void jjtAddChild(ASTNode n, int i) {
		switch(i) {
		case 2:
			if( n instanceof ASTConstExpression ) {
				Object val = ((ASTConstExpression)n).val;
				if( val == null || !( val instanceof Number) )
					throw new CompilerException(n.getPos(),"Priority must be of int type, but found "+n);
				prior = ((Number)val).intValue();
				if( prior < 0 || prior > 255 )
					throw new CompilerException(n.getPos(),"Operator priority must have value from 0 to 255");
				pos = n.getPos();
				return;
			}
			break;
		case 1:
			opmode = -1;
			if( n instanceof ASTIdentifier ) {
				KString optype = ((ASTIdentifier)n).name;
				for(int i=0; i < Operator.orderAndArityNames.length; i++) {
					if( Operator.orderAndArityNames[i].equals(optype) ) {
						opmode = i;
						break;
					}
				}
				if( opmode < 0 )
					throw new CompilerException(n.getPos(),"Operator mode must be one of "+Arrays.toString(Operator.orderAndArityNames));
				return;
			}
			break;
		case 0:
			if( n instanceof ASTOperator ) {
				image = ((ASTOperator)n).image;
				return;
			}
			else if( n instanceof ASTIdentifier ) {
				image = ((ASTIdentifier)n).name;
				return;
			}
			break;
		}
		throw new CompilerException(n.getPos(),"Bad child number "+i+": "+n);
    }

	public void pass2_2() {
		switch(opmode) {
		case Operator.LFY:
			{
				AssignOperator op = AssignOperator.getOperator(image);
				if (op != null) {
					if (prior != op.priority)
						throw new CompilerException(pos,"Operator declaration conflict: priority "+prior+" and "+op.priority+" are different");
					if (opmode != op.mode)
						throw new CompilerException(pos,"Operator declaration conflict: "+Operator.orderAndArityNames[opmode]+" and "+Operator.orderAndArityNames[op.mode]+" are different");
					return;
				}
				op = AssignOperator.newAssignOperator(image,null,null);
				if( Kiev.verbose ) System.out.println("Declared assign operator "+op+" "+Operator.orderAndArityNames[op.mode]+" "+op.priority);
			}
			break;
		case Operator.XFX:
		case Operator.YFX:
		case Operator.XFY:
		case Operator.YFY:
			{
				BinaryOperator op = BinaryOperator.getOperator(image);
				if (op != null) {
					if (prior != op.priority)
						throw new CompilerException(pos,"Operator declaration conflict: priority "+prior+" and "+op.priority+" are different");
					if (opmode != op.mode)
						throw new CompilerException(pos,"Operator declaration conflict: "+Operator.orderAndArityNames[opmode]+" and "+Operator.orderAndArityNames[op.mode]+" are different");
					return;
				}
				op = BinaryOperator.newBinaryOperator(prior,image,null,null,Operator.orderAndArityNames[opmode]);
				if( Kiev.verbose ) System.out.println("Declared infix operator "+op+" "+Operator.orderAndArityNames[op.mode]+" "+op.priority);
			}
			break;
		case Operator.FX:
		case Operator.FY:
			{
				PrefixOperator op = PrefixOperator.getOperator(image);
				if (op != null) {
					if (prior != op.priority)
						throw new CompilerException(pos,"Operator declaration conflict: priority "+prior+" and "+op.priority+" are different");
					if (opmode != op.mode)
						throw new CompilerException(pos,"Operator declaration conflict: "+Operator.orderAndArityNames[opmode]+" and "+Operator.orderAndArityNames[op.mode]+" are different");
					return;
				}
				op = PrefixOperator.newPrefixOperator(prior,image,null,null,Operator.orderAndArityNames[opmode]);
				if( Kiev.verbose ) System.out.println("Declared prefix operator "+op+" "+Operator.orderAndArityNames[op.mode]+" "+op.priority);
			}
			break;
		case Operator.XF:
		case Operator.YF:
			{
				PostfixOperator op = PostfixOperator.getOperator(image);
				if (op != null) {
					if (prior != op.priority)
						throw new CompilerException(pos,"Operator declaration conflict: priority "+prior+" and "+op.priority+" are different");
					if (opmode != op.mode)
						throw new CompilerException(pos,"Operator declaration conflict: "+Operator.orderAndArityNames[opmode]+" and "+Operator.orderAndArityNames[op.mode]+" are different");
					return;
				}
				op = PostfixOperator.newPostfixOperator(prior,image,null,null,Operator.orderAndArityNames[opmode]);
				if( Kiev.verbose ) System.out.println("Declared postfix operator "+op+" "+Operator.orderAndArityNames[op.mode]+" "+op.priority);
			}
			break;
		case Operator.XFXFY:
			throw new CompilerException(pos,"Multioperators are not supported yet");
		default:
			throw new CompilerException(pos,"Unknown operator mode "+opmode);
		}
	}

	public String toString() {
		return image.toString();
	}

	public Dumper toJava(Dumper dmp) {
		return dmp.space().append("/* alias operator(")
			.append(Integer.toString(prior)).append(",")
			.append(Operator.orderAndArityNames[opmode]).append(",")
			.append(image).append(") */").space();
	}

}
