package kiev.parser;

import kiev.vlang.*;
import kiev.stdlib.*;

class JJTkiev020State {
  private java.util.Stack nodes;
  private java.util.Stack marks;

  private int sp;		// number of nodes on stack
  private int mk;		// current mark
  private boolean node_created;

  JJTkiev020State() {
    nodes = new java.util.Stack();
    marks = new java.util.Stack();
    sp = 0;
    mk = 0;
  }

  /* Determines whether the current node was actually closed and
     pushed.  This should only be called in the final user action of a
     node scope.  */
  boolean nodeCreated() {
    return node_created;
  }

  /* Call this to reinitialize the node stack.  It is called
     automatically by the parser's ReInit() method. */
  void reset() {
    nodes.removeAllElements();
    marks.removeAllElements();
    sp = 0;
    mk = 0;
  }

  /* Returns the root node of the AST.  It only makes sense to call
     this after a successful parse. */
  ASTNode rootNode() {
    return (ASTNode)nodes.elementAt(0);
  }

  /* Pushes a node on to the stack. */
  void pushNode(ASTNode n) {
//    System.out.println("AST push "+n.getClass());
    nodes.push(n);
    ++sp;
  }

  /* Returns the node on the top of the stack, and remove it from the
     stack.  */
  ASTNode popNode() {
    if (--sp < mk) {
      mk = ((Integer)marks.pop()).intValue();
    }
    if( nodes.size()!=0 )
      return (ASTNode)nodes.pop();
    else
      return null;
  }

  /* Returns the node currently on the top of the stack. */
  ASTNode peekNode() {
    if( nodes.size()!=0 )
      return (ASTNode)nodes.peek();
    else
      return null;
  }

  /* Returns the number of children on the stack in the current node
     scope. */
  int nodeArity() {
    return sp - mk;
  }


  void clearNodeScope(ASTNode n) {
//    System.out.println("AST clear node scope "+n.getClass());
    while (sp > mk) {
      popNode();
    }
    if( marks.empty() )
    	mk = 0;
    else
    	mk = ((Integer)marks.pop()).intValue();
  }


  void openNodeScope(ASTNode n) {
//    System.out.println("AST open node scope "+n.getClass());
    marks.push(new Integer(mk));
    mk = sp;
//    n.jjtOpen();
  }


  /* A definite node is constructed from a specified number of
     children.  That number of nodes are popped from the stack and
     made the children of the definite node.  Then the definite node
     is pushed on to the stack. */
  void closeNodeScope(ASTNode n, int num) {
//    System.out.print("AST close node scope "+n.getClass());
    mk = ((Integer)marks.pop()).intValue();
    ASTNode[] ns = new ASTNode[num];
    while (--num >= 0) {
      ns[num] = popNode();
      ns[num].jjtSetParent(n);
    }
    for(int i=0; i < ns.length; i++) {
//      System.out.println("AST add node "+ns[i].getClass()+" to "+n.getClass());
      n.jjtAddChild(ns[i], i);
    }
//  n.jjtClose();
    pushNode(n);
    node_created = true;
  }


  /* A conditional node is constructed if its condition is true.  All
     the nodes that have been pushed since the node was opened are
     made children of the the conditional node, which is then pushed
     on to the stack.  If the condition is false the node is not
     constructed and they are left on the stack. */
  void closeNodeScope(ASTNode n, boolean condition) {
//    System.out.println("AST close node scope "+condition+" "+n.getClass());
    if (condition) {
      int a = nodeArity();
      mk = ((Integer)marks.pop()).intValue();
      ASTNode[] ns = new ASTNode[a];
      while (--a >= 0) {
        ns[a] = popNode();
        ns[a].jjtSetParent(n);
      }
      for(int i=0; i < ns.length; i++) {
//        System.out.println("AST add node "+ns[i].getClass()+" to "+n.getClass());
        n.jjtAddChild(ns[i], i);
      }
//    n.jjtClose();
      pushNode(n);
      node_created = true;
    } else {
      mk = ((Integer)marks.pop()).intValue();
      node_created = false;
    }
  }
}
